"use strict";(self.webpackChunkclassic=self.webpackChunkclassic||[]).push([[316],{5988:e=>{e.exports=JSON.parse('{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"sidebar":[{"type":"link","label":"Overview","href":"/pebblo/","docId":"introduction","unlisted":false},{"type":"link","label":"Installation","href":"/pebblo/installation","docId":"installation","unlisted":false},{"type":"link","label":"Development Environment","href":"/pebblo/development","docId":"development","unlisted":false},{"type":"link","label":"Pebblo Daemon","href":"/pebblo/daemon","docId":"daemon","unlisted":false},{"type":"link","label":"Pebblo Safe DataLoader for Langchain","href":"/pebblo/rag","docId":"rag","unlisted":false},{"type":"link","label":"Reports","href":"/pebblo/reports","docId":"reports","unlisted":false}]},"docs":{"daemon":{"id":"daemon","title":"Pebblo Daemon","description":"Pebblo Daemon is a REST API application that exposes API endpoints for Pebblo Safe DataLoader to connect. This component provides deep data visibility on the types of Topics and Entities ingested into the Gen-AI application. It uses the snippets received from the Pebblo Safe DataLoader to run through both a Topic Classifier and Entity Classifier to produce the insights and reporting. For more details on how to Pebblo enable your Langchain application see this Pebblo Safe DataLoader for Langchain document.","sidebar":"sidebar"},"development":{"id":"development","title":"Setting up development environment","description":"Pebblo is currently supported in MacOS and Linux.","sidebar":"sidebar"},"installation":{"id":"installation","title":"Installation","description":"Pre-requisites","sidebar":"sidebar"},"introduction":{"id":"introduction","title":"Overview","description":"Pebblo enables developers to safely load data and promote their Gen AI app to deployment without worrying about the organization\u2019s compliance and security requirements. The project identifies semantic topics and entities found in the loaded data and summarizes them on the UI or a PDF report.","sidebar":"sidebar"},"rag":{"id":"rag","title":"Pebblo Safe DataLoader for Langchain","description":"This document describes how to augment your existing Langchain DocumentLoader with Pebblo Safe DataLoader to get deep data visibility on the types of Topics and Entities ingested into the Gen-AI Langchain application. For details on Pebblo Daemon see this pebblo daemon document.","sidebar":"sidebar"},"reports":{"id":"reports","title":"Pebblo Data Reports","description":"Pebblo Data Reports provides an in-depth visibilty into the document ingested into Gen-AI RAG application during every load.","sidebar":"sidebar"}}}')}}]);